import React from 'react';

const SensorAlerts = ({ sensors, onSelectSensor }) => {
  const alerts = sensors.filter((s) => s.status === 'alert');

  return (
    <div className="bg-white shadow-md rounded-2xl p-4 h-full overflow-auto">
      <h2 className="text-xl font-bold mb-4 text-gray-800">Alertas Activas</h2>
      {alerts.length === 0 ? (
        <p className="text-gray-500">No hay alertas activas.</p>
      ) : (
        <ul className="space-y-3">
          {alerts.map((alert) => (
            <li
              key={alert.id}
              className="border border-red-400 bg-red-50 p-3 rounded-lg cursor-pointer hover:bg-red-100 transition"
              onClick={() => onSelectSensor(alert)}
            >
              <p className="font-semibold text-red-700">{alert.name}</p>
              <p className="text-sm text-gray-700">Regi√≥n: {alert.region}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SensorAlerts;
