import React from 'react';

const SensorAlerts = ({ sensors = [], onSelectSensor = () => {} }) => {
  const critical = sensors.filter((s) => s.status === 'critical');
  const warning = sensors.filter((s) => s.status === 'warning');
  const normal = sensors.filter((s) => s.status === 'normal');

  const formatDate = (d) => new Date(d).toLocaleString('es-CL', { hour12: false });

  return (
    <div className="bg-white border border-gray-200 rounded-3xl p-6 shadow-xl h-full overflow-y-auto fade-in">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-3">üîî Alertas Actuales</h2>

      {critical.length > 0 && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-xl">
          <h3 className="text-xl font-semibold text-red-600 mb-3">‚ö†Ô∏è Cr√≠ticas</h3>
          {critical.map((s) => (
            <div
              key={s.id}
              className="bg-red-100 border border-red-300 rounded-lg p-3 mb-2 cursor-pointer hover:bg-red-200"
              onClick={() => onSelectSensor(s)}
            >
              <p className="font-bold text-red-700">{s.name}</p>
              <p className="text-red-600 text-sm">Nivel: {s.value}%</p>
              <p className="text-xs text-red-500">√öltima actualizaci√≥n: {formatDate(s.lastUpdate)}</p>
            </div>
          ))}
        </div>
      )}

      {warning.length > 0 && (
        <div className="mb-6 p-4 bg-yellow-50 border border-yellow-200 rounded-xl">
          <h3 className="text-xl font-semibold text-yellow-600 mb-3">‚ö†Ô∏è Advertencias</h3>
          {warning.map((s) => (
            <div
              key={s.id}
              className="bg-yellow-100 border border-yellow-300 rounded-lg p-3 mb-2 cursor-pointer hover:bg-yellow-200"
              onClick={() => onSelectSensor(s)}
            >
              <p className="font-bold text-yellow-700">{s.name}</p>
              <p className="text-yellow-600 text-sm">Nivel: {s.value}%</p>
              <p className="text-xs text-yellow-500">√öltima actualizaci√≥n: {formatDate(s.lastUpdate)}</p>
            </div>
          ))}
        </div>
      )}

      {normal.length > 0 && (
        <div className="p-4 bg-green-50 border border-green-200 rounded-xl">
          <h3 className="text-xl font-semibold text-green-600 mb-3">‚úÖ Normales</h3>
          {normal.map((s) => (
            <div
              key={s.id}
              className="bg-green-100 border border-green-300 rounded-lg p-3 mb-2 cursor-pointer hover:bg-green-200"
              onClick={() => onSelectSensor(s)}
            >
              <p className="font-bold text-green-700">{s.name}</p>
              <p className="text-green-600 text-sm">Nivel: {s.value}%</p>
              <p className="text-xs text-green-500">√öltima actualizaci√≥n: {formatDate(s.lastUpdate)}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SensorAlerts;
