import React, { useState } from 'react';

const ChileMap = ({ sensors = [], onSelectSensor = () => {} }) => {
  const [tooltipContent, setTooltipContent] = useState('');
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  const markerPositions = {
    '1': { top: '25%', left: '50%' },
    '2': { top: '45%', left: '48%' },
    '3': { top: '65%', left: '52%' },
  };

  const colorScale = (value) => {
    if (value < 30) return '#ef4444';
    if (value < 60) return '#facc15';
    return '#22c55e';
  };

  const handleMouseEnter = (sensor) => {
    setTooltipContent(`${sensor.name} - Nivel: ${sensor.value}% - Estado: ${sensor.status.toUpperCase()}`);
  };

  const handleMouseLeave = () => setTooltipContent('');
  const handleMouseMove = (e) => setTooltipPosition({ x: e.clientX + 10, y: e.clientY + 10 });

  return (
    <div
      className="relative bg-white border border-gray-200 rounded-3xl p-6 shadow-xl overflow-hidden h-full flex flex-col fade-in"
      onMouseMove={handleMouseMove}
    >
      <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-3">
        游늸 Mapa de Chile (Simplificado)
      </h2>

      <div
        className="relative flex-grow w-full min-h-[400px] bg-gradient-to-b from-gray-100 to-gray-300 rounded-2xl border border-gray-200 overflow-hidden"
        style={{
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
        }}
      >
        {sensors.map((sensor) => {
          const position = markerPositions[sensor.id] || { top: '50%', left: '50%' };
          const markerColor = colorScale(sensor.value);
          const icon = sensor.status === 'critical' ? '丘멆잺' : sensor.status === 'warning' ? '游리' : '游릭';

          return (
            <div
              key={sensor.id}
              className="absolute cursor-pointer transition-transform duration-200 hover:scale-125"
              style={{ ...position, transform: 'translate(-50%, -50%)' }}
              onMouseEnter={() => handleMouseEnter(sensor)}
              onMouseLeave={handleMouseLeave}
              onClick={() => onSelectSensor(sensor)}
            >
              <div
                className="w-10 h-10 rounded-full flex items-center justify-center text-xl"
                style={{ backgroundColor: markerColor, color: 'white' }}
              >
                {icon}
              </div>
            </div>
          );
        })}
      </div>

      {tooltipContent && (
        <div
          className="absolute bg-gray-800 text-white text-sm px-3 py-2 rounded-lg shadow-lg pointer-events-none z-50 max-w-xs"
          style={{ left: tooltipPosition.x, top: tooltipPosition.y }}
        >
          {tooltipContent}
        </div>
      )}

      <div className="mt-4 pt-4 border-t border-gray-200 flex justify-center gap-8 text-sm">
        <div className="flex items-center gap-1">
          <div className="w-4 h-4 bg-red-500 rounded-full"></div>
          <span>Cr칤tico (&lt;30%)</span>
        </div>
        <div className="flex items-center gap-1">
          <div className="w-4 h-4 bg-yellow-500 rounded-full"></div>
          <span>Advertencia (30-60%)</span>
        </div>
        <div className="flex items-center gap-1">
          <div className="w-4 h-4 bg-green-500 rounded-full"></div>
          <span>Normal (&gt;60%)</span>
        </div>
      </div>
    </div>
  );
};

export default ChileMap;
