import React, { useState, useEffect } from "react";
import ChileMap from "./components/ChileMap";
import SensorAlerts from "./components/SensorAlerts";
import SensorDetail from "./components/SensorDetail";
import { defaultSensors } from "./mock/sensors";
import "./App.css";

// 🧠 Determina el estado en base al valor
const determineStatus = (value) => {
  if (value < 30) return "critical";
  if (value < 60) return "warning";
  return "normal";
};

// 🔁 Aplica el estado dinámico a los sensores
const applyStatusToSensors = (sensorList) => {
  return sensorList.map((sensor) => ({
    ...sensor,
    status: determineStatus(sensor.value),
  }));
};

const App = () => {
  const [sensors, setSensors] = useState(applyStatusToSensors(defaultSensors));
  const [selectedSensor, setSelectedSensor] = useState(null);

  const handleSelectSensor = (sensor) => setSelectedSensor(sensor);
  const handleCloseDetail = () => setSelectedSensor(null);

  // 🕒 Simulación: actualizar valores cada 10 segundos (en producción vendrán del sistema real)
  useEffect(() => {
    const interval = setInterval(() => {
      setSensors((prev) =>
        prev.map((s) => {
          // simulamos una variación leve del value
          const newValue = Math.max(0, Math.min(100, s.value + (Math.random() * 10 - 5)));
          return { ...s, value: newValue, status: determineStatus(newValue) };
        })
      );
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 p-4 md:p-8 flex flex-col relative">
      <div className="text-center mb-8 fade-in">
        <h1 className="text-4xl font-extrabold text-gray-900 mb-2 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-700">
          Sistema de Monitoreo de Sensores
        </h1>
        <p className="text-lg text-gray-600">
          Visualiza el estado de tus centrales en tiempo real y gestiona alertas.
        </p>
      </div>

      <div className="flex flex-col lg:flex-row flex-grow gap-8 container mx-auto max-w-6xl">
        <div className="lg:w-1/2 h-[600px] lg:h-auto relative">
          <ChileMap sensors={sensors} onSelectSensor={handleSelectSensor} />
        </div>
        <div className="lg:w-1/2 h-[600px] lg:h-auto">
          <SensorAlerts sensors={sensors} onSelectSensor={handleSelectSensor} />
        </div>
      </div>

      {selectedSensor && (
        <SensorDetail sensor={selectedSensor} onClose={handleCloseDetail} />
      )}
    </div>
  );
};

export default App;
