import React from 'react';

const SensorDetail = ({ sensor, onClose }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-11/12 md:w-1/2 fade-in">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-2xl font-bold text-gray-800">{sensor.name}</h3>
          <button
            onClick={onClose}
            className="text-gray-600 hover:text-red-600 text-lg font-bold"
          >
            ✕
          </button>
        </div>
        <p className="text-gray-700 mb-2">
          <strong>Región:</strong> {sensor.region}
        </p>
        <p className="text-gray-700 mb-2">
          <strong>Estado:</strong>{' '}
          <span
            className={
              sensor.status === 'alert'
                ? 'text-red-600 font-semibold'
                : 'text-green-600 font-semibold'
            }
          >
            {sensor.status === 'alert' ? 'Alerta' : 'Normal'}
          </span>
        </p>
        <p className="text-gray-700">
          <strong>Última lectura:</strong> {sensor.value}
        </p>
      </div>
    </div>
  );
};

export default SensorDetail;
