import React from 'react';

const SensorDetail = ({ sensor, onClose = () => {} }) => {
  if (!sensor) return null;

  const colorMap = {
    critical: { bg: 'bg-red-100', text: 'text-red-700', emoji: '‚ö†Ô∏è' },
    warning: { bg: 'bg-yellow-100', text: 'text-yellow-700', emoji: 'üü°' },
    normal: { bg: 'bg-green-100', text: 'text-green-700', emoji: '‚úÖ' },
  };

  const color = colorMap[sensor.status] || colorMap.normal;

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50 fade-in"
      onClick={onClose}
    >
      <div
        className="bg-white rounded-3xl p-8 shadow-2xl max-w-lg w-full relative slide-up"
        onClick={(e) => e.stopPropagation()}
      >
        <button
          onClick={onClose}
          className="absolute top-4 right-4 p-2 rounded-full bg-gray-100 hover:bg-gray-200 text-gray-700 font-bold"
        >
          ‚úï
        </button>

        <div className="flex items-center gap-3 mb-6">
          <div className={`p-3 rounded-full ${color.bg} text-2xl`}>{color.emoji}</div>
          <div>
            <h3 className="text-3xl font-bold text-gray-900">{sensor.name}</h3>
            <p className={`text-lg font-semibold ${color.text}`}>Estado: {sensor.status.toUpperCase()}</p>
          </div>
        </div>

        <div className="space-y-3 text-gray-700">
          <p><strong>üìç Regi√≥n:</strong> {sensor.region}</p>
          <p><strong>üìà Nivel:</strong> {sensor.value}%</p>
          <p><strong>‚è∞ √öltima actualizaci√≥n:</strong> {new Date(sensor.lastUpdate).toLocaleString('es-CL')}</p>

          {sensor.status === 'critical' && (
            <div className="bg-red-50 border border-red-300 text-red-800 p-4 rounded-xl mt-4">
              ‚ö†Ô∏è <strong>¬°Alerta cr√≠tica!</strong> Requiere mantenimiento inmediato.
            </div>
          )}
          {sensor.status === 'warning' && (
            <div className="bg-yellow-50 border border-yellow-300 text-yellow-800 p-4 rounded-xl mt-4">
              ‚ö†Ô∏è Nivel bajo. Considerar revisi√≥n pronto.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SensorDetail;
