import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

const ChileMap = ({ sensors, onSelectSensor }) => {
  const icon = (color) =>
    new L.Icon({
      iconUrl: `https://chart.googleapis.com/chart?chst=d_map_pin_icon&chld=flag|${color}`,
      iconSize: [30, 40],
      iconAnchor: [15, 40],
    });

  // Límites aproximados de Chile (norte, sur, este, oeste)
  const chileBounds = L.latLngBounds(
    L.latLng(-56, -76), // suroeste (Magallanes)
    L.latLng(-17, -66)  // noreste (Arica)
  );

  return (
    <div className="rounded-3xl border border-gray-200 shadow-xl overflow-hidden h-[600px]">
      <MapContainer
        center={[-35.6751, -71.543]} // centro de Chile
        zoom={5}
        minZoom={4.5}
        maxZoom={10}
        maxBounds={chileBounds}
        maxBoundsViscosity={1.0} // impide salir del país
        style={{ width: "100%", height: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />

        {sensors.map((s) => (
          <Marker
            key={s.id}
            position={[s.location.latitude, s.location.longitude]}
            icon={icon(
              s.status === "critical"
                ? "red"
                : s.status === "warning"
                ? "yellow"
                : "green"
            )}
            eventHandlers={{
              click: () => onSelectSensor(s),
            }}
          >
            <Popup>
              <strong>{s.name}</strong>
              <br />
              Estado: {s.status}
              <br />
              Valor: {s.value}%
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default ChileMap;
