import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import ChileMap from './components/ChileMap';
import SensorAlerts from './components/SensorAlerts';
import SensorDetail from './components/SensorDetail';
import { defaultSensors } from './mock/sensors';

const App = () => {
  const [sensors, setSensors] = useState(defaultSensors);
  const [selectedSensor, setSelectedSensor] = useState(null);

  const handleSelectSensor = (sensor) => {
    setSelectedSensor(sensor);
  };

  const handleCloseDetail = () => {
    setSelectedSensor(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 p-4 md:p-8 flex flex-col">
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-center mb-8"
      >
        <h1 className="text-4xl font-extrabold text-gray-900 mb-2 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-700">
          Sistema de Monitoreo de Sensores
        </h1>
        <p className="text-lg text-gray-600">
          Visualiza el estado de tus centrales en tiempo real y gestiona alertas.
        </p>
      </motion.div>

      <div className="flex flex-col lg:flex-row flex-grow gap-8 container mx-auto max-w-6xl">
        <div className="lg:w-1/2 h-[600px] lg:h-auto"> {/* Altura fija para el mapa en móviles, auto en desktop */}
          <ChileMap sensors={sensors} onSelectSensor={handleSelectSensor} />
        </div>
        <div className="lg:w-1/2 h-[600px] lg:h-auto"> {/* Altura fija para alertas en móviles, auto en desktop */}
          <SensorAlerts sensors={sensors} onSelectSensor={handleSelectSensor} />
        </div>
      </div>

      <AnimatePresence>
        {selectedSensor && (
          <SensorDetail sensor={selectedSensor} onClose={handleCloseDetail} />
        )}
      </AnimatePresence>
    </div>
  );
};

export default App;
