import React, { useState } from "react";
import chileMap from "../assets/chile-map.png"; // aseg칰rate de tener la imagen en /src/assets

const ChileMap = ({ sensors, onSelectSensor }) => {
  const [tooltip, setTooltip] = useState({ text: "", x: 0, y: 0 });

  const colorScale = (value) => {
    if (value < 30) return "#ef4444";
    if (value < 60) return "#facc15";
    return "#22c55e";
  };

  // Posiciones relativas sobre la imagen
  const markerPositions = {
    "1": { top: "30%", left: "45%" }, // Santiago
    "2": { top: "55%", left: "43%" }, // Concepci칩n
    "3": { top: "75%", left: "47%" }, // Puerto Montt
  };

  return (
    <div
      className="relative bg-white border border-gray-200 rounded-3xl p-6 shadow-xl h-full flex flex-col fade-in"
      onMouseMove={(e) =>
        setTooltip((t) => ({ ...t, x: e.clientX + 10, y: e.clientY + 10 }))
      }
    >
      <h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2">
        游늸 Mapa de Chile
      </h2>

      <div className="relative flex-grow overflow-hidden rounded-2xl border border-gray-200 bg-gray-100">
        <img
          src={chileMap}
          alt="Mapa de Chile"
          className="w-full h-full object-contain opacity-90"
        />

        {sensors.map((s) => {
          const pos = markerPositions[s.id] || { top: "50%", left: "50%" };
          const color = colorScale(s.value);
          const icon =
            s.status === "critical" ? "丘멆잺" : s.status === "warning" ? "游리" : "游릭";

          return (
            <div
              key={s.id}
              className="absolute cursor-pointer transition-transform hover:scale-125"
              style={{ ...pos, transform: "translate(-50%, -50%)" }}
              onClick={() => onSelectSensor(s)}
              onMouseEnter={() =>
                setTooltip({
                  text: `${s.name} - ${s.value}% (${s.status})`,
                  x: 0,
                  y: 0,
                })
              }
              onMouseLeave={() => setTooltip({ text: "", x: 0, y: 0 })}
            >
              <div
                className="w-8 h-8 rounded-full flex items-center justify-center text-lg"
                style={{ backgroundColor: color }}
              >
                {icon}
              </div>
            </div>
          );
        })}
      </div>

      {tooltip.text && (
        <div
          className="absolute bg-gray-800 text-white text-sm px-3 py-2 rounded-lg shadow-lg pointer-events-none"
          style={{ left: tooltip.x, top: tooltip.y }}
        >
          {tooltip.text}
        </div>
      )}

      <div className="mt-4 pt-4 border-t border-gray-200 flex justify-center gap-6 text-sm">
        <div className="flex items-center gap-1">
          <div className="w-4 h-4 bg-red-500 rounded-full" /> Cr칤tico
        </div>
        <div className="flex items-center gap-1">
          <div className="w-4 h-4 bg-yellow-500 rounded-full" /> Advertencia
        </div>
        <div className="flex items-center gap-1">
          <div className="w-4 h-4 bg-green-500 rounded-full" /> Normal
        </div>
      </div>
    </div>
  );
};

export default ChileMap;
