import React, { useState } from "react";
import ChileMap from "./components/ChileMap";
import SensorAlerts from "./components/SensorAlerts";
import SensorDetail from "./components/SensorDetail";
import { defaultSensors } from "./mock/sensors";
import "./App.css";

// 🧠 Determina el estado en base al valor
const determineStatus = (value) => {
  if (value < 30) return "critical";
  if (value < 60) return "warning";
  return "normal";
};

// 🔁 Aplica el estado dinámico a los sensores
const applyStatusToSensors = (sensorList) => {
  return sensorList.map((sensor) => ({
    ...sensor,
    status: determineStatus(sensor.value),
  }));
};

const App = () => {
  const [sensors, setSensors] = useState(applyStatusToSensors(defaultSensors));
  const [selectedSensor, setSelectedSensor] = useState(null);

  const handleSelectSensor = (sensor) => setSelectedSensor(sensor);
  const handleCloseDetail = () => setSelectedSensor(null);

  return (
    <div className="h-screen w-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 flex flex-col overflow-hidden">
      {/* Header */}
      <header className="py-4 text-center">
        <h1 className="text-3xl md:text-4xl font-extrabold text-gray-900 mb-1 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-700">
          Sistema de Monitoreo de Sensores
        </h1>
        <p className="text-gray-600 text-base md:text-lg">
          Visualiza el estado de tus centrales en tiempo real.
        </p>
      </header>

      {/* Contenido principal (mapa + alertas) */}
      <main className="flex-grow flex flex-col lg:flex-row justify-center items-stretch gap-6 px-6 pb-6">
        <div className="w-full lg:w-1/2 h-[calc(100vh-160px)] lg:h-[80vh] relative">
          <ChileMap sensors={sensors} onSelectSensor={handleSelectSensor} />
        </div>
        <div className="w-full lg:w-1/2 h-[calc(100vh-160px)] lg:h-[80vh] overflow-y-auto rounded-3xl border border-gray-200 shadow-lg bg-white">
          <SensorAlerts sensors={sensors} onSelectSensor={handleSelectSensor} />
        </div>
      </main>

      {/* Panel flotante del detalle del sensor */}
      {selectedSensor && (
        <SensorDetail sensor={selectedSensor} onClose={handleCloseDetail} />
      )}
    </div>
  );
};

export default App;
